import React, { useState, useEffect } from 'react';
import { Container, Title, Header, Footer,ViewCreateCustomer,  ButtonCreateCustomer, TitleCreateCustomer, ListCustomers } from '../../component/indexCustomers';
import { ButtonHeader, ButtonComeBack } from '../../component/buttonComeBack';
import FindByAllCustomers from './FindByAllCustomers';
import { useNavigation } from '@react-navigation/native';
import Api from '../../services';
import { Entypo } from '@expo/vector-icons';
import { Ionicons } from '@expo/vector-icons';


interface CustomersData {
	id: number;
	name: string;
	purchase: string;
	price: number;
	amount: number;
	total: number;
}

export default function Customer() {
	const navigation = useNavigation();
	const [ customers, setCustomers ] = useState<CustomersData[]>([]);

	useEffect(()=> {
		Api.get<CustomersData[]>('/customer')
		.then(response => {
			setCustomers(response.data.Customers);
		})
	}, [])

	const numColumns = 2;

	return (
		<Container>
			<Header>
				<ButtonHeader>
					<ButtonComeBack onPress = {()=> navigation.navigate("Home")} >
						<Ionicons name="ios-arrow-round-back" size={40} color="white" />
					</ButtonComeBack>
				</ButtonHeader>

				<Title> VocÃª possui {customers.length} clientes: </Title>

				<ViewCreateCustomer>
					<ButtonCreateCustomer onPress = {()=> navigation.navigate("CreateCustomer")} >
						<TitleCreateCustomer> Adicionar cliente: </TitleCreateCustomer>
						<Entypo name="circle-with-plus" size={32} color="white" />
					</ButtonCreateCustomer>
				</ViewCreateCustomer>
			</Header>

			<Footer>
				<ListCustomers data = { customers } keyExtractor = { item => item.id }
				renderItem = {({ item }) => (
					<FindByAllCustomers data = { item } />
				)}
				numColumns = {numColumns} />
			</Footer>
		</Container>
	);
}
